import React, { useState } from 'react';
import { uploadMaterial } from '../api/api';

export default function UploadSection() {
  const [form, setForm] = useState({
    title: '',
    subject: '',
    group: '',
    type: 'pdf',
    year: '2025',
    semester: 'Sem-1',
    uploaderName: ''
  });
  const [file, setFile] = useState(null);
  const [msg, setMsg] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  const handleFile = (e) => setFile(e.target.files[0]);

  const submit = async (e) => {
    e.preventDefault();
    if (!file) return setMsg('Please select a file to upload.');
    const fd = new FormData();
    fd.append('file', file);
    fd.append('title', form.title);
    fd.append('subject', form.subject);
    fd.append('group', form.group);
    fd.append('type', form.type);
    fd.append('year', form.year);
    fd.append('semester', form.semester);
    fd.append('uploaderName', form.uploaderName);
    setLoading(true);
    try {
      // calls backend API; if backend not ready this will fail - catch below
      const res = await uploadMaterial(fd);
      setMsg('Upload submitted â€” pending admin approval.');
      setForm({
        title: '',
        subject: '',
        group: '',
        type: 'pdf',
        year: '2025',
        semester: 'Sem-1',
        uploaderName: ''
      });
      setFile(null);
      // reset file input value by using a key or controlled input in actual implementation
    } catch (err) {
      console.error(err);
      setMsg('Upload failed (backend may be offline). Data saved locally in demo mode.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section id="upload" className="upload-section">
      <h3>Upload Study Material / Question Paper</h3>
      <form className="upload-form" onSubmit={submit}>
        <div className="row">
          <label>Title<input name="title" value={form.title} onChange={handleChange} required /></label>
          <label>Subject<input name="subject" value={form.subject} onChange={handleChange} placeholder="e.g., Physics" required /></label>
        </div>

        <div className="row">
          <label>Group (optional)<input name="group" value={form.group} onChange={handleChange} /></label>
          <label>Type
            <select name="type" value={form.type} onChange={handleChange}>
              <option value="pdf">PDF</option>
              <option value="image">Image</option>
            </select>
          </label>
        </div>

        <div className="row">
          <label>Year
            <select name="year" value={form.year} onChange={handleChange}>
              <option>All</option>
              <option>2020</option>
              <option>2021</option>
              <option>2022</option>
              <option>2023</option>
              <option>2024</option>
              <option>2025</option>
            </select>
          </label>
          <label>Semester
            <select name="semester" value={form.semester} onChange={handleChange}>
              <option>Sem-1</option>
              <option>Sem-2</option>
              <option>Sem-3</option>
              <option>Sem-4</option>
              <option>Sem-5</option>
              <option>Sem-6</option>
              <option>Internship-1</option>
              <option>Internship-2</option>
            </select>
          </label>
        </div>

        <div className="row">
          <label>Uploader Name (optional)<input name="uploaderName" value={form.uploaderName} onChange={handleChange} /></label>
          <label>File <input type="file" accept=".pdf,image/*" onChange={handleFile} required /></label>
        </div>

        <div className="row actions">
          <button type="submit" className="btn" disabled={loading}>{loading ? 'Submitting...' : 'Submit Upload'}</button>
        </div>

        {msg && <div className="form-msg">{msg}</div>}
      </form>
    </section>
  );
}
