import React, { useState, useEffect } from 'react';

const updates = [
  "üöÄ New feature: Now you can filter materials by year and type!",
  "üìö Semester 6 materials are now available!",
  "üéØ Improved file upload system with better preview",
  "üîî Real-time notifications for new uploads"
];

export default function UpdatePopup() {
  const [showPopup, setShowPopup] = useState(true); // Changed to true for testing
  const [currentUpdate, setCurrentUpdate] = useState(0);

  // For testing, show immediately. Remove this useEffect for production
  // useEffect(() => {
  //   const timer = setTimeout(() => {
  //     setShowPopup(true);
  //   }, 2000);
  //   return () => clearTimeout(timer);
  // }, []);

  const closePopup = () => setShowPopup(false);
  
  const nextUpdate = () => {
    if (currentUpdate < updates.length - 1) {
      setCurrentUpdate(prev => prev + 1);
    } else {
      closePopup();
    }
  };
  
  const prevUpdate = () => {
    if (currentUpdate > 0) {
      setCurrentUpdate(prev => prev - 1);
    }
  };

  if (!showPopup) return null;

  return (
    <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)', zIndex: 9999 }}>
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header bg-primary text-white">
            <h5 className="modal-title">üéâ What's New</h5>
            <button 
              type="button" 
              className="btn-close btn-close-white" 
              onClick={closePopup}
            ></button>
          </div>
          <div className="modal-body text-center py-4">
            <p className="lead fw-bold">{updates[currentUpdate]}</p>
            <small className="text-muted">
              {currentUpdate + 1} of {updates.length}
            </small>
          </div>
          <div className="modal-footer justify-content-between">
            <button 
              className="btn btn-outline-secondary" 
              onClick={prevUpdate}
              disabled={currentUpdate === 0}
            >
              ‚Üê Previous
            </button>
            <button 
              className="btn btn-primary" 
              onClick={nextUpdate}
            >
              {currentUpdate === updates.length - 1 ? 'Finish' : 'Next ‚Üí'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
