import React, { useState, useEffect } from 'react';

const updates = [
  "🚀 New feature: Now you can filter materials by year and type!",
  "📚 Semester 6 materials are now available!",
  "🎯 Improved file upload system with better preview",
  "🔔 Real-time notifications for new uploads"
];

 function UpdatePopup() {
  const [showPopup, setShowPopup] = useState(false);
  const [currentUpdate, setCurrentUpdate] = useState(0);

  useEffect(() => {
    // Check if popup was shown before using localStorage
    const hasSeenPopup = localStorage.getItem('hasSeenPopup');
    
    if (!hasSeenPopup) {
      // Show popup after 2 seconds on first visit
      const timer = setTimeout(() => {
        setShowPopup(true);
      }, 2000);

      return () => clearTimeout(timer);
    }
  }, []);

  const closePopup = () => {
    setShowPopup(false);
    // Remember that user has seen the popup
    localStorage.setItem('hasSeenPopup', 'true');
  };

  const nextUpdate = () => {
    if (currentUpdate < updates.length - 1) {
      setCurrentUpdate(prev => prev + 1);
    } else {
      closePopup(); // Close when reached end
    }
  };

  const prevUpdate = () => {
    if (currentUpdate > 0) {
      setCurrentUpdate(prev => prev - 1);
    }
  };

  if (!showPopup) return null;

  return (
    <div className="modal show d-block" tabIndex="-1" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          <div className="modal-header bg-primary text-white">
            <h5 className="modal-title">🎉 What's New</h5>
            <button 
              type="button" 
              className="btn-close btn-close-white" 
              onClick={closePopup}
              aria-label="Close"
            ></button>
          </div>
          <div className="modal-body text-center py-4">
            <div className="update-content">
              <div className="mb-3">
                <i className="fas fa-bell fa-2x text-primary mb-2"></i>
              </div>
              <p className="lead fw-bold">{updates[currentUpdate]}</p>
              <small className="text-muted">
                {currentUpdate + 1} of {updates.length}
              </small>
            </div>
          </div>
          <div className="modal-footer justify-content-between">
            <button 
              className="btn btn-outline-secondary" 
              onClick={prevUpdate}
              disabled={currentUpdate === 0}
            >
              ← Previous
            </button>
            <div>
              <button 
                className="btn btn-secondary me-2" 
                onClick={closePopup}
              >
                Skip All
              </button>
              <button 
                className="btn btn-primary" 
                onClick={nextUpdate}
              >
                {currentUpdate === updates.length - 1 ? 'Finish' : 'Next →'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
// Should end with:
export default UpdatePopup;
