import React, { useState, useEffect } from 'react';

const examSchedule = [
  { time: '09:00 AM', date: 'Mar 15', day: 'Friday', subject: 'Mathematics', type: 'Regular', duration: '3 Hours' },
  { time: '11:30 AM', date: 'Mar 15', day: 'Friday', subject: 'Physics', type: 'Regular', duration: '3 Hours' },
  { time: '02:00 PM', date: 'Mar 15', day: 'Friday', subject: 'Chemistry', type: 'Supply', duration: '3 Hours' },
  { time: '09:00 AM', date: 'Mar 16', day: 'Saturday', subject: 'Computer Science', type: 'Regular', duration: '3 Hours' },
  { time: '11:30 AM', date: 'Mar 16', day: 'Saturday', subject: 'English', type: 'Regular', duration: '3 Hours' },
  { time: '02:00 PM', date: 'Mar 16', day: 'Saturday', subject: 'Mathematics', type: 'Supply', duration: '3 Hours' }
];

export default function HeroSection() {
  const [currentDateIndex, setCurrentDateIndex] = useState(0);
  const uniqueDates = [...new Set(examSchedule.map(exam => exam.date))];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDateIndex((prev) => (prev + 1) % uniqueDates.length);
    }, 4000); // Change every 4 seconds

    return () => clearInterval(interval);
  }, [uniqueDates.length]);

  const currentDate = uniqueDates[currentDateIndex];
  const todayExams = examSchedule.filter(exam => exam.date === currentDate);

  return (
    <section className="hero-section">
      <div className="container">
        <div className="hero-content">
          <div className="timetable-container">
            <div className="timetable-header">
              <h1 className="timetable-title">
                <i className="bi bi-calendar-week"></i>
                Exam Timetable
              </h1>
              <p className="timetable-subtitle">
                Stay updated with your exam schedule
              </p>
            </div>

            {/* Current Date Display */}
            <div className="date-display">
              <div className="current-date">
                <div className="date-badge">
                  <i className="bi bi-calendar-check"></i>
                  {currentDate}
                </div>
                <div className="day-name">{todayExams[0]?.day}</div>
              </div>
              <div className="date-indicator">
                {uniqueDates.map((date, index) => (
                  <div
                    key={date}
                    className={`indicator-dot ${index === currentDateIndex ? 'active' : ''}`}
                  ></div>
                ))}
              </div>
            </div>

            {/* Exams List */}
            <div className="exams-list">
              {todayExams.map((exam, index) => (
                <div key={index} className="exam-card">
                  <div className="exam-time-section">
                    <div className="exam-time">{exam.time}</div>
                    <div className="exam-duration">{exam.duration}</div>
                  </div>
                  
                  <div className="exam-details">
                    <div className="exam-subject">{exam.subject}</div>
                    <div className="exam-type-badge">
                      <span className={`type-dot ${exam.type.toLowerCase()}`}></span>
                      {exam.type} Exam
                    </div>
                  </div>
                  
                  <div className="exam-status">
                    <div className={`status-indicator ${exam.type.toLowerCase()}`}>
                      {exam.type === 'Regular' ? 'Scheduled' : 'Retake'}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Quick Stats */}
            <div className="timetable-stats">
              <div className="stat-item">
                <div className="stat-number">{examSchedule.length}</div>
                <div className="stat-label">Total Exams</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">
                  {examSchedule.filter(e => e.type === 'Regular').length}
                </div>
                <div className="stat-label">Regular</div>
              </div>
              <div className="stat-item">
                <div className="stat-number">
                  {examSchedule.filter(e => e.type === 'Supply').length}
                </div>
                <div className="stat-label">Supply</div>
              </div>
            </div>

            {/* Actions */}
            <div className="timetable-actions">
              <button className="action-btn download">
                <i className="bi bi-download"></i>
                Download PDF
              </button>
              <button className="action-btn reminder">
                <i className="bi bi-bell"></i>
                Set Reminder
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
