import React from 'react';

const features = [
  {
    icon: 'bi-journals',
    title: 'Semester-wise Materials',
    description: 'Organized study resources for every semester with easy filtering and search.'
  },
  {
    icon: 'bi-file-earmark-pdf',
    title: 'Question Papers',
    description: 'Previous year question papers to help you prepare effectively for exams.'
  },
  {
    icon: 'bi-cloud-upload',
    title: 'Easy Upload',
    description: 'Share your study materials with fellow students through our simple upload system.'
  },
  {
    icon: 'bi-briefcase',
    title: 'Internship Resources',
    description: 'Find internship opportunities and preparation materials for your career growth.'
  }
];

export default function FeaturesSection() {
  return (
    <section className="features-section">
      <div className="container">
        <h2 className="section-title">Everything You Need</h2>
        <p className="section-subtitle">
          Comprehensive resources tailored for computer science students to excel in academics and career.
        </p>
        
        <div className="row g-4">
          {features.map((feature, index) => (
            <div key={index} className="col-md-6 col-lg-3">
              <div className="feature-card">
                <div className="feature-icon">
                  <i className={`bi ${feature.icon}`}></i>
                </div>
                <h4>{feature.title}</h4>
                <p>{feature.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
