import React from 'react';

export default function MaterialsList({ items = [] }) {
  if (!items.length) {
    return (
      <div className="no-items">
        <img src="/assets/coming-soon.png" alt="coming soon" />
        <p>No materials found — coming soon</p>
      </div>
    );
  }

  return (
    <div className="materials-list">
      {items.map((it) => (
        <div className="material-card" key={it.id || it.title}>
          <div className="material-meta">
            <h4>{it.title}</h4>
            <div className="small">
              <span>{it.subject}</span> • <span>{it.semester}</span> • <span>{it.year || 'All'}</span>
            </div>
            <p className="desc">{it.description || ''}</p>
          </div>
          <div className="material-actions">
            {it.fileUrl ? (
              <>
                <a className="btn" href={it.fileUrl} target="_blank" rel="noreferrer">Preview</a>
                <a className="btn" href={it.fileUrl} download>Download</a>
              </>
            ) : (
              <span className="pending">Pending approval</span>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}
