import React from 'react';

const updates = [
  'New: Sem-4 Digital Electronics notes uploaded!',
  'Reminder: Internship applications available — check Internship 1',
  'New: Question paper set 2024 uploaded for Sem-2 Physics',
  'Site maintenance scheduled Sunday 10 PM'
];

export default function UpdatesTicker() {
  return (
    <div className="updates-ticker">
      <div className="ticker-track">
        {updates.map((u, i) => (
          <div className="tick" key={i}>
            {u}
          </div>
        ))}
      </div>
    </div>
  );
}
