import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

export default function Header() {
  const [scrolled, setScrolled] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const isActive = (path) => location.pathname === path;

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      alert(`Searching for: ${searchQuery}`); // You can implement actual search later
      setSearchQuery('');
      setShowSearch(false);
    }
  };

  return (
    <>
      <nav className={`navbar navbar-expand-lg fixed-top ${scrolled ? 'navbar-scrolled' : ''}`}>
        <div className="container">
          {/* Left Navigation - 3 Lines */}
          <div className="navbar-nav me-auto">
            <Link 
              className={`nav-link ${isActive('/') ? 'active' : ''}`} 
              to="/"
            >
              Home
            </Link>
            <Link 
              className={`nav-link ${isActive('/semesters') ? 'active' : ''}`} 
              to="/semesters"
            >
              Semesters
            </Link>
            <a 
              className="nav-link" 
              href="#upload"
            >
              Upload
            </a>
          </div>

          {/* Center Logo - Big & Prominent */}
          <Link className="navbar-brand mx-auto" to="/">
            <div className="brand-center">
              <img src="/assets/logo.png" alt="StudentHub" className="logo-large" />
              <span className="brand-text">StudentHub</span>
            </div>
          </Link>

          {/* Right Side - Search & Actions */}
          <div className="navbar-nav ms-auto">
            {/* Search Icon/Bar */}
            <div className="nav-item search-container">
              {showSearch ? (
                <form className="search-form" onSubmit={handleSearch}>
                  <div className="input-group">
                    <input
                      type="text"
                      className="form-control search-input"
                      placeholder="Search materials..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      autoFocus
                    />
                    <button 
                      className="btn btn-outline-secondary" 
                      type="button"
                      onClick={() => setShowSearch(false)}
                    >
                      <i className="bi bi-x"></i>
                    </button>
                    <button className="btn btn-primary" type="submit">
                      <i className="bi bi-search"></i>
                    </button>
                  </div>
                </form>
              ) : (
                <button 
                  className="nav-link search-toggle"
                  onClick={() => setShowSearch(true)}
                >
                  <i className="bi bi-search"></i>
                </button>
              )}
            </div>
          </div>
        </div>
      </nav>

      {/* Spacer for fixed header */}
      <div style={{ height: '80px' }}></div>
    </>
  );
}
