import React from 'react';
import { Link } from 'react-router-dom';

const semesters = [
  { slug: 'sem-1', title: 'Semester 1' },
  { slug: 'sem-2', title: 'Semester 2' },
  { slug: 'sem-3', title: 'Semester 3' },
  { slug: 'sem-4', title: 'Semester 4' },
  { slug: 'sem-5', title: 'Semester 5' },
  { slug: 'sem-6', title: 'Semester 6' },
  { slug: 'internship-1', title: 'Internship 1' },
  { slug: 'internship-2', title: 'Internship 2' }
];

export default function SemestersGrid() {
  return (
    <section className="semesters-grid">
      {semesters.map((s) => (
        <Link to={`/semester/${s.slug}`} key={s.slug} className="sem-tile">
          <div className="sem-image" />
          <div className="sem-title">{s.title}</div>
        </Link>
      ))}
    </section>
  );
}
