import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

export default function Header() {
  const [scrolled, setScrolled] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const isActive = (path) => location.pathname === path;

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      alert(`Searching for: ${searchQuery}`);
      setSearchQuery('');
      setShowSearch(false);
    }
  };

  const handleUploadClick = (e) => {
    e.preventDefault();
    if (location.pathname === '/') {
      // Scroll to upload section on home page
      const uploadSection = document.getElementById('upload');
      if (uploadSection) {
        uploadSection.scrollIntoView({ behavior: 'smooth' });
      }
    } else {
      // Navigate to home and then scroll to upload
      navigate('/');
      setTimeout(() => {
        const uploadSection = document.getElementById('upload');
        if (uploadSection) {
          uploadSection.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    }
  };

  return (
    <>
      <nav className={`navbar navbar-expand-lg fixed-top ${scrolled ? 'navbar-scrolled' : ''}`}>
        <div className="container">
          {/* Left Side - 3 Navigation Items */}
          <div className="d-none d-lg-flex navbar-nav me-auto">
            <Link 
              className={`nav-link no-underline ${isActive('/') ? 'active' : ''}`} 
              to="/"
            >
              Home
            </Link>
            <Link 
              className={`nav-link no-underline ${isActive('/semesters') ? 'active' : ''}`} 
              to="/semesters"
            >
              Semesters
            </Link>
            <a 
              className="nav-link no-underline" 
              href="#upload"
              onClick={handleUploadClick}
            >
              Upload
            </a>
          </div>

          {/* Center Logo */}
          <Link className="navbar-brand mx-auto" to="/">
            <div className="brand-center">
              <div className="logo-large">
                <i className="bi bi-journals"></i>
              </div>
              <span className="brand-text">StudentHub</span>
            </div>
          </Link>

          {/* Right Side - Search */}
          <div className="d-none d-lg-flex navbar-nav ms-auto">
            <div className="nav-item search-container">
              {showSearch ? (
                <form className="search-form" onSubmit={handleSearch}>
                  <div className="input-group">
                    <input
                      type="text"
                      className="form-control search-input"
                      placeholder="Search materials..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      autoFocus
                    />
                    <button 
                      className="btn btn-outline-secondary" 
                      type="button"
                      onClick={() => setShowSearch(false)}
                    >
                      <i className="bi bi-x"></i>
                    </button>
                    <button className="btn btn-primary" type="submit">
                      <i className="bi bi-search"></i>
                    </button>
                  </div>
                </form>
              ) : (
                <button 
                  className="nav-link search-toggle no-underline"
                  onClick={() => setShowSearch(true)}
                >
                  <i className="bi bi-search"></i> Search
                </button>
              )}
            </div>
          </div>

          {/* Mobile Hamburger Menu */}
          <button 
            className="navbar-toggler d-lg-none" 
            type="button" 
            data-bs-toggle="collapse" 
            data-bs-target="#navbarMobile"
          >
            <span className="navbar-toggler-icon"></span>
          </button>

          {/* Mobile Menu */}
          <div className="collapse navbar-collapse d-lg-none" id="navbarMobile">
            <div className="navbar-nav">
              <Link 
                className={`nav-link no-underline ${isActive('/') ? 'active' : ''}`} 
                to="/"
              >
                Home
              </Link>
              <Link 
                className={`nav-link no-underline ${isActive('/semesters') ? 'active' : ''}`} 
                to="/semesters"
              >
                Semesters
              </Link>
              <a 
                className="nav-link no-underline" 
                href="#upload"
                onClick={handleUploadClick}
              >
                Upload
              </a>
              <div className="nav-item">
                <button 
                  className="nav-link no-underline"
                  onClick={() => setShowSearch(true)}
                >
                  <i className="bi bi-search me-2"></i>
                  Search
                </button>
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* Spacer for fixed header */}
      <div style={{ height: '80px' }}></div>

      {/* Fullscreen Search Overlay */}
      {showSearch && (
        <div className="search-overlay">
          <div className="search-overlay-content">
            <div className="container">
              <div className="row justify-content-center">
                <div className="col-md-8">
                  <div className="search-header">
                    <h3>Search Materials</h3>
                    <button 
                      className="btn-close"
                      onClick={() => setShowSearch(false)}
                    ></button>
                  </div>
                  <form onSubmit={handleSearch}>
                    <div className="input-group mb-3">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Search for notes, question papers, subjects..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        autoFocus
                      />
                      <button className="btn btn-primary btn-lg" type="submit">
                        <i className="bi bi-search"></i> Search
                      </button>
                    </div>
                  </form>
                  <div className="search-suggestions">
                    <h6>Popular Searches:</h6>
                    <div className="suggestion-tags">
                      <span className="badge bg-light text-dark">Physics Notes</span>
                      <span className="badge bg-light text-dark">Maths Question Papers</span>
                      <span className="badge bg-light text-dark">Semester 4</span>
                      <span className="badge bg-light text-dark">Internship</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
