import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

export default function Header() {
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const location = useLocation();
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      alert(`Searching for: ${searchQuery}`);
      setSearchQuery('');
      setShowSearch(false);
    }
  };

  const handleUploadClick = (e) => {
    e.preventDefault();
    if (location.pathname === '/') {
      const uploadSection = document.getElementById('upload');
      if (uploadSection) {
        uploadSection.scrollIntoView({ behavior: 'smooth' });
      }
    } else {
      navigate('/');
      setTimeout(() => {
        const uploadSection = document.getElementById('upload');
        if (uploadSection) {
          uploadSection.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    }
  };

  return (
    <>
      <nav className="navbar fixed-top">
        <div className="container">
          {/* Left Side - Navigation */}
          <div className="nav-left">
            <Link className="nav-item" to="/">
              Home
            </Link>
            <Link className="nav-item" to="/semesters">
              Semesters
            </Link>
            <a className="nav-item" href="#upload" onClick={handleUploadClick}>
              Upload
            </a>
          </div>

          {/* Center - Logo */}
          <div className="nav-center">
            <Link to="/" className="logo-link">
              <img src="/assets/logo.png" alt="StudentHub" className="logo-img" />
              <span className="logo-text">StudentHub</span>
            </Link>
          </div>

          {/* Right Side - Search */}
          <div className="nav-right">
            <button 
              className="search-btn"
              onClick={() => setShowSearch(true)}
            >
              <i className="bi bi-search"></i>
              Search
            </button>
          </div>
        </div>
      </nav>

      {/* Spacer for fixed header */}
      <div className="header-spacer"></div>

      {/* Search Overlay */}
      {showSearch && (
        <div className="search-overlay">
          <div className="search-modal">
            <div className="search-header">
              <h4>Search Materials</h4>
              <button 
                className="close-btn"
                onClick={() => setShowSearch(false)}
              >
                <i className="bi bi-x-lg"></i>
              </button>
            </div>
            <form onSubmit={handleSearch} className="search-form">
              <div className="input-group">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search notes, question papers, subjects..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  autoFocus
                />
                <button className="btn btn-primary" type="submit">
                  Search
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </>
  );
}
