import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

export default function Header() {
  const [showSearch, setShowSearch] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const location = useLocation();
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      alert(`Searching for: ${searchQuery}`);
      setSearchQuery('');
      setShowSearch(false);
    }
  };

  const handleUploadClick = (e) => {
    e.preventDefault();
    if (location.pathname === '/') {
      const uploadSection = document.getElementById('upload');
      if (uploadSection) {
        uploadSection.scrollIntoView({ behavior: 'smooth' });
      }
    } else {
      navigate('/');
      setTimeout(() => {
        const uploadSection = document.getElementById('upload');
        if (uploadSection) {
          uploadSection.scrollIntoView({ behavior: 'smooth' });
        }
      }, 100);
    }
  };

  return (
    <>
      <nav className="navbar fixed-top">
        <div className="container">
          {/* Left Side - Navigation */}
          <div className="nav-left">
            <Link className="nav-item" to="/">
              Home
            </Link>
            <Link className="nav-item" to="/semesters">
              Semesters
            </Link>
            <a className="nav-item" href="#upload" onClick={handleUploadClick}>
              Upload
            </a>
          </div>

          {/* Center - Logo Text */}
          <div className="nav-center">
            <Link to="/" className="logo-link">
              <div className="logo-text-container">
                <div className="college-name">MR COLLEGE AUTONOMOUS</div>
                <div className="college-caption">ONLY FOR STUDENTS</div>
              </div>
            </Link>
          </div>

          {/* Right Side - Search Bar */}
          <div className="nav-right">
            <form className="search-bar-form" onSubmit={handleSearch}>
              <div className="search-input-container">
                <i className="bi bi-search search-icon"></i>
                <input
                  type="text"
                  className="search-input"
                  placeholder="Search materials..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            </form>
          </div>
        </div>
      </nav>

      {/* Spacer for fixed header */}
      <div className="header-spacer"></div>

      {/* Search Popup */}
      {showSearch && (
        <div className="search-popup-overlay">
          <div className="search-popup">
            <div className="search-popup-header">
              <h4>Search Study Materials</h4>
              <button 
                className="search-popup-close"
                onClick={() => setShowSearch(false)}
              >
                <i className="bi bi-x-lg"></i>
              </button>
            </div>
            
            <form onSubmit={handleSearch} className="search-popup-form">
              <div className="search-popup-input-container">
                <i className="bi bi-search search-popup-icon"></i>
                <input
                  type="text"
                  className="search-popup-input"
                  placeholder="Search notes, question papers, subjects, semester..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  autoFocus
                />
                <button className="btn btn-primary search-popup-btn" type="submit">
                  Search
                </button>
              </div>
            </form>

            <div className="search-suggestions">
              <h6>Quick Search:</h6>
              <div className="suggestion-tags">
                <span className="suggestion-tag" onClick={() => setSearchQuery('Physics Notes')}>Physics Notes</span>
                <span className="suggestion-tag" onClick={() => setSearchQuery('Maths Question Papers')}>Maths Papers</span>
                <span className="suggestion-tag" onClick={() => setSearchQuery('Semester 4')}>Semester 4</span>
                <span className="suggestion-tag" onClick={() => setSearchQuery('Internship')}>Internship</span>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}
